targets.query:
from(bucket: v.defaultBucket)
  |> range(start: -10m)
  |> filter(fn: (r) => r._measurement == "p110_energy_consumption" and r._field == "today_energy")
  |> last()
  |> group(columns: ["host"])
  |> sum()
  |> map(fn: (r) => ({r with
    _value: float(v: r._value / 1000) * ${price_per_kwh} + ${standing_charge}
  }))

import "date"

from(bucket: v.defaultBucket)
  |> range(start: -10m)
  |> filter(fn: (r) => r._measurement == "p110_energy_consumption" and r._field == "month_energy")
  |> last()
  |> group(columns: ["host"])
  |> sum()
  |> map(fn: (r) => ({r with
    _value: float(v: r._value / 1000) * ${price_per_kwh} + float(v: date.monthDay(t: now())) * ${standing_charge}
  }))
